CREATE SEQUENCE leaderboard_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1;

CREATE TABLE IF NOT EXISTS public.leaderboard
(
    id integer NOT NULL DEFAULT nextval('leaderboard_id_seq'::regclass),
    network character varying(15) COLLATE pg_catalog."default" NOT NULL,
    address character varying(42) COLLATE pg_catalog."default" NOT NULL,
    name character varying(150) COLLATE pg_catalog."default" NOT NULL,
    hits numeric NOT NULL,
    updated_at timestamp NOT NULL DEFAULT now(),
    CONSTRAINT leaderboard_pkey PRIMARY KEY (id)
)

TABLESPACE pg_default;

ALTER TABLE IF EXISTS public.leaderboard
    OWNER to postgres;

GRANT ALL ON TABLE public.leaderboard TO anon;

GRANT ALL ON TABLE public.leaderboard TO authenticated;

GRANT ALL ON TABLE public.leaderboard TO postgres;

GRANT ALL ON TABLE public.leaderboard TO service_role;

create extension if not exists moddatetime schema extensions;

-- assuming the table name is "todos", and a timestamp column "updated_at"
-- this trigger will set the "updated_at" column to the current timestamp for every update
create trigger handle_updated_at before update on leaderboard
  for each row execute procedure moddatetime (updated_at);
